    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        int numero, divisor = 0, divisorM = 1,rpta;
        numero = ingresarN();
        rpta = calcularDivisorM(numero,divisor,divisorM);
        mostrar(rpta);
        
    }//GEN-LAST:event_btnProcesarActionPerformed

    int ingresarN(){
        return Integer.parseInt(txtNumero.getText());
    }
    int calcularDivisorM(int numero, int divisor, int divisorM){
        for(int i=1;i<=numero;i++){
            if(numero%i == 0){
                divisor = i;
                if(divisor>divisorM && divisor != numero){
                    divisorM = divisor;
                }else{
                    divisorM = divisorM;
                }
            }
        } return divisorM;
    }
    void imprimir(String texto){
        txtSalida.append(texto+"\n");
    }
    void mostrar(int divisor){
        imprimir("Mayor divisor: "+divisor);
    }
