    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        // Declaro
        int cantidad, noculto;
        double precio,descuento,bruto,neto;
        //Asigno
        cantidad = Integer.parseInt(txtCantidad.getText());
        noculto = Integer.parseInt(txtOculto.getText());
        precio = Integer.parseInt(txtPrecio.getText());
        //Opero
        bruto = precio*cantidad;
        
        if(noculto%2 == 0 && noculto >= 100){
            descuento = bruto*0.15;
        }else{
            descuento = bruto * 0.05;
        }
        
        neto = bruto-descuento;
        //Muestro
        txtSalida.setText("El precio Burto es: "+bruto+"\n");
        txtSalida.append("El importe de descuento es: "+descuento+"\n"+"El importe a pagar es: "+neto);
    }//GEN-LAST:event_btnProcesarActionPerformed

