    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMostrarActionPerformed
        // TODO add your handling code here:
        String letra,mensaje;
        letra = txtLetra.getText();
        
        switch(letra){
            case "a": mensaje = "Es vocal";
                break;
            case "e" : mensaje = "Es vocal";
                break;
            case "i" : mensaje = "Es vocal";
                break;
            case "o" : mensaje = "Es vocal";
                break;
            case "u" : mensaje = "Es vocal";
                break;
            default: mensaje = "Es consonante";
        }
        
        txtSalida.setText("Se ingres√≥ una: "+mensaje);
    }//GEN-LAST:event_btnMostrarActionPerformed

